import React from 'react';
import UserDropdown from './UserDropdown';

function TodoList({ todos, users, onUpdate, onDelete, onAssign, onViewHistory }) {
  return (
    <div className="todo-list">
      {todos.map(todo => (
        <div key={todo.todoId} className="todo-card">
          <h3>
            {todo.title}
            {todo.isActive
              ? <span className="active-tag">Active</span>
              : <span className="inactive-tag">Inactive</span>}
          </h3>
          <ul>
            {todo.listOfItems.map((item, i) => <li key={i}>{item}</li>)}
          </ul>
          <p>Status: <strong>{todo.status}</strong></p>
          <div className="todo-actions">
            <button onClick={() => {
              const newTitle = prompt("Edit title:", todo.title);
              if (newTitle) onUpdate(todo.todoId, { ...todo, title: newTitle });
            }}>Edit</button>
            <button onClick={() => onDelete(todo.todoId)}>Delete</button>
            <UserDropdown users={users} onAssign={(userId) => onAssign(todo.todoId, userId)} />
            <button onClick={() => onViewHistory(todo.todoId)}>View History</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default TodoList;
